
var app = angular.module("myApp", []);

app.directive("mySaludo", function () {
    return {
        scope: {
            nombre: '=nombre'
        },
        compile: function (tElem, tAttrs) {
            console.log('compile => ' + tElem.html());
            return {
                pre: function (scope, iElem, iAttrs) {
                    console.log('pre link => ' + iElem.html());
                },
                post: function (scope, iElem, iAttrs) {
                    console.log('post link => ' + iElem.html());
                }

            }
        },
        template: "<p>hola {{nombre}}</p>"
    };
});

app.controller("myControl", ['$scope', function ($scope) {
    $scope.nombresanto = "Santito";
}]);